import React, { useState, useEffect } from 'react';
import FormEditor from './components/FormEditor';
import FormPreview from './components/FormPreview';

function App() {
  const [forms, setForms] = useState([]);
  const [currentForm, setCurrentForm] = useState(null);
  const [isPreview, setIsPreview] = useState(false);

  useEffect(() => {
    fetchForms();
  }, []);

  const fetchForms = async () => {
    try {
      const response = await fetch(`http://localhost:8932/api/forms`);
      const data = await response.json();
      setForms(data);
    } catch (error) {
      console.error('Error fetching forms:', error);
    }
  };


  const handleFormSave = async (form) => {
    try {
      const response = await fetch(`http://localhost:8932/api/forms`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(form),
      });
      const savedForm = await response.json();
      setForms([...forms, savedForm]);
      setCurrentForm(savedForm);
    } catch (error) {
      console.error('Error saving form:', error);
    }
  };

  const togglePreview = () => {
    setIsPreview(!isPreview);
  };

  console.log(currentForm)

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Form Builder</h1>
      <button
        onClick={togglePreview}
        className="bg-blue-500 text-white px-4 py-2 rounded mb-4"
      >
        {isPreview ? 'Edit Form' : 'Preview Form'}
      </button>
      {isPreview ? (
        <FormPreview form={currentForm} />
      ) : (
        <FormEditor onSave={handleFormSave} />
      )}
    </div>
  );
}

export default App;
