import React, { useState, useEffect } from "react";

const FormEditor = ({ form, onSave }) => {
  const [title, setTitle] = useState("");
  const [questions, setQuestions] = useState([]);

  useEffect(() => {
    if (form) {
      // Prefill fields if editing an existing form
      setTitle(form.title || "");
      setQuestions(form.questions || []);
    } else {
      // Reset fields for a new form
      setTitle("");
      setQuestions([]);
    }
  }, [form]);

  const handleSave = () => {
    const newForm = { title, questions };
    if (form && form._id) {
      newForm._id = form._id; // Preserve the ID for editing
    }
    onSave(newForm);
  };

  return (
    <div>
      <h2 className="text-2xl font-bold mb-4">{form ? "Edit Form" : "Create New Form"}</h2>
      <div className="mb-4">
        <label className="block font-semibold mb-2">Form Title:</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="border rounded px-2 py-1 w-full"
        />
      </div>
      {/* Add logic for questions (not shown for brevity) */}
      <button
        onClick={handleSave}
        className="bg-blue-500 text-white px-4 py-2 rounded"
      >
        Save Form
      </button>
    </div>
  );
};

export default FormEditor;
